import React, { useEffect, useState } from 'react'
import Layout from 'Layouts'
import VideosActivity from "./partials/VideosActivity"
import PeopleChannel from "./partials/PeopleChannel"
import fetch from 'isomorphic-unfetch';

const Home = () => {
  const [batman, setBatman] = useState([])
  const [cast, setCast] = useState([])
  useEffect(() => {
    const fetchbatman = fetch(`https://api.tvmaze.com/search/shows?q=batman`, {
      method: 'GET',
    })

    const fetchcast = fetch(`http://api.tvmaze.com/shows/1/cast`, {
      method: 'GET',
    })

    return Promise.all([fetchbatman, fetchcast])
      .then(values => Promise.all(values.map(value => value.json())))
      .then(finalValues => {
        setBatman(finalValues[0].show)
        setCast(finalValues[1])
      })
  }, [])
  return (
    < Layout >
      <VideosActivity batman={batman} />
      <PeopleChannel cast={cast} />
    </Layout >
  )
}

export default Home
