import React from 'react'
import { Container, Row, Col } from 'reactstrap'
import styled from 'styled-components'
import { MainVideosView, ActivityView } from 'Containers'

const Wrapper = styled.section`
  background:  #fff2fe; 
  padding: 2rem 0;
`

const VideosActivity = ({ shows }) => {
  console.log(shows)
  return (
    <Wrapper>
      <Container>
        <Row>
          <Col md="8">
            <MainVideosView shows={shows} title="Videos" subtitle="Browse All Videos" moreIcon="cloud_upload" moreLabel="Upload Your Own Videos" />
          </Col>
          <ActivityView />
        </Row>
      </Container>
    </Wrapper>
  )
}

VideosActivity.getInitialProps = async function () {
  const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
  const data = await res.json();

  return {
    shows: data.map(entry => entry)
  };
};

export default VideosActivity
